import styled from "styled-components";

export const Container = styled.div`
  padding: 20px;
  display: flex;
  flex-wrap: wrap;
`;

export const PetsCard = styled.div`
  padding: 10px;
  border: ${({ theme }) => `1px solid ${theme.primary}`};
  width: calc(33.3% - 22px);
`;
