import styled from "styled-components";

export const Container = styled.div`
  padding: 20px;
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
`;

export const PetsCard = styled.div`
  padding: 10px;
  border: ${({ theme }) => `1px solid ${theme.primary}`};
  width: calc(33.3% - 35px);
  border-radius: 20px;
`;

export const FilterRow = styled.div`
  display: flex;
  align-items: center;
`;
